import { useEffect, useState } from "react";
import WifiService from "../../services/wifi.service";
import "./wifi.scss";

const Wifi = () => {
  const [items, setItems] = useState([]);
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [boardNo, setBoardNo] = useState("");

  const onSubmit = () => {
    WifiService.updateWifi(name, password, boardNo);
    console.log({ name, password, boardNo });
  };

  useEffect(() => {
    WifiService.getWifi().then((res) => {
      setItems(res.data);
    });
  }, []);

  useEffect(() => {
    setName(items.name_wifi);
    setPassword(items.password_wifi);
    setBoardNo(items.board_no);
  }, [items]);

  return (
    <div className="wifi">
      <div className="wifi__grid">
        <div className="wifi__header">ตั้งค่า Wifi</div>
        {items.map((item) => {
          return (
            <>
              <div className="wifi__box">
                <div className="wifi__item">ชื่อ wifi</div>
                <input
                  type="text"
                  id="name"
                  name="name"
                  onChange={(name) => setName(name.target.value)}
                  defaultValue={item.name_wifi}
                ></input>
                <div className="wifi__item">รหัสผ่าน</div>
                <input
                  type="text"
                  id="password"
                  name="password"
                  onChange={(password) => setPassword(password.target.value)}
                  defaultValue={item.password_wifi}
                ></input>
                <div className="wifi__item">หมายเลขบอร์ด</div>
                <input
                  type="text"
                  id="boardNo"
                  name="boardNo"
                  onChange={(boardNo) => setBoardNo(boardNo.target.value)}
                  defaultValue={item.num_board}
                ></input>
              </div>
            </>
          );
        })}
        <button className="wifi__button" onClick={onSubmit}>
          บันทึก
        </button>
      </div>
    </div>
  );
};

export default Wifi;
